/**
 * frontend.js
 *
 * @author Your Inspiration Themes
 * @package YITH WooCommerce Zoom Magnifier
 */
jQuery(document).ready(function ($) {

    var yith_wcmg = $('.product-detail-page .img');
    var yith_wcmg_zoom = $('.yith_magnifier_zoom');
    var yith_wcmg_image = $('.yith_magnifier_zoom img');

    var yith_wcmg_default_zoom = yith_wcmg.find('.yith_magnifier_zoom').attr('href');
    var yith_wcmg_default_image = yith_wcmg.find('.yith_magnifier_zoom img').attr('src');
    var yith_magnifier_options = {
        zoomImageWidth: 1000,
        zoomImageHeight: 1000,
        zoomWidth: 500,
        zoomHeight: 500,
        lensOpacity: 0.9,
        showTitle: false,
        elements: {
            zoom: yith_wcmg_zoom,
            zoomImage: yith_wcmg_image.first()
        }
    }

    if( typeof yith_magnifier_options == 'undefined' ) {
        return false;
    }

    yith_wcmg.yith_magnifier(yith_magnifier_options);

    $(document).on('found_variation', 'form.variations_form', function (event, variation) {
        var image_magnifier = variation.image_magnifier ? variation.image_magnifier : yith_wcmg_default_zoom;
        var image_src = variation.image_src ? variation.image_src : yith_wcmg_default_image;

        yith_wcmg_zoom.attr('href', image_magnifier);
        yith_wcmg_image.attr('src', image_src);
        yith_wcmg_image.attr('srcset', image_src);
        yith_wcmg_image.attr('src-orig', image_src);

        if (yith_wcmg.data('yith_magnifier')) {
            yith_wcmg.yith_magnifier('destroy');
        }

        yith_wcmg.yith_magnifier(yith_magnifier_options);
    }).on('reset_image', function (event) {
        yith_wcmg_zoom.attr('href', yith_wcmg_default_zoom);
        yith_wcmg_image.attr('src', yith_wcmg_default_image);
        yith_wcmg_image.attr('srcset', yith_wcmg_default_image);
        yith_wcmg_image.attr('src-orig', yith_wcmg_default_image);

        if (yith_wcmg.data('yith_magnifier')) {
            yith_wcmg.yith_magnifier('destroy');
        }

        yith_wcmg.yith_magnifier(yith_magnifier_options);
    });




    $('ul.products>li').each(function() {
        var yith_wcmg = $(this);
        var yith_wcmg_zoom = $(this).find('.yith_magnifier_zoom');
        var yith_wcmg_image = $(this).find('.yith_magnifier_zoom img');

        var yith_wcmg_default_zoom = yith_wcmg.find('.yith_magnifier_zoom').attr('href');
        var yith_wcmg_default_image = yith_wcmg.find('.yith_magnifier_zoom img').attr('src');
        var yith_magnifier_options = {
            zoomImageWidth: 1000,
            zoomImageHeight: 1000,
            zoomWidth: 500,
            zoomHeight: 500,
            lensOpacity: 0.9,
            showTitle: false,
            elements: {
                zoom: yith_wcmg_zoom,
                zoomImage: yith_wcmg_image.first()
            }
        }

        if( typeof yith_magnifier_options == 'undefined' ) {
            return false;
        }

        yith_wcmg.yith_magnifier(yith_magnifier_options);

        $(document).on('found_variation', 'form.variations_form', function (event, variation) {
            var image_magnifier = variation.image_magnifier ? variation.image_magnifier : yith_wcmg_default_zoom;
            var image_src = variation.image_src ? variation.image_src : yith_wcmg_default_image;

            yith_wcmg_zoom.attr('href', image_magnifier);
            yith_wcmg_image.attr('src', image_src);
            yith_wcmg_image.attr('srcset', image_src);
            yith_wcmg_image.attr('src-orig', image_src);

            if (yith_wcmg.data('yith_magnifier')) {
                yith_wcmg.yith_magnifier('destroy');
            }

            yith_wcmg.yith_magnifier(yith_magnifier_options);
        }).on('reset_image', function (event) {
            yith_wcmg_zoom.attr('href', yith_wcmg_default_zoom);
            yith_wcmg_image.attr('src', yith_wcmg_default_image);
            yith_wcmg_image.attr('srcset', yith_wcmg_default_image);
            yith_wcmg_image.attr('src-orig', yith_wcmg_default_image);

            if (yith_wcmg.data('yith_magnifier')) {
                yith_wcmg.yith_magnifier('destroy');
            }

            yith_wcmg.yith_magnifier(yith_magnifier_options);
        });
    });
});